function deleteImage(e){$.post(imageGallery.config.ajaxUrl,{tokenCSRF:$("#jstokenCSRF").val(),action:"deleteImage",album:$(e).data("album"),file:$(e).data("file")},function(){let a="#imagegallery-image-"+$(e).data("number");$(a).hide()}).fail(function(){$.alert({title:imageGallery.L.error,content:imageGallery.L.deleteImageError})})}$(function(){$(".delete-image").bind("click",function(){confirm("Are you sure?")&&deleteImage(this)})});